proc calcEulerN[T](correctAnswer : T, fn: () -> T): void =
    let e1_1 = getMonoTime()
    let res = fn()
    let e1_2 = getMonoTime() - e1_1
    echo($(res) & (if (correctAnswer == res): " - OK [" else: " - ERROR [") & $(e1_2) & "]")

calcEulerN(233168, proc () : int = euler1(1000))
